function waitForElements(selectors, callback) {
    // console.log('waiting for elements');
  var total = 0;

  setTimeout(function waitForElementsFn() {
    if (total === 75 || !(callback && typeof callback === 'function') || !(selectors && selectors.length)) return;
      // console.log('set timeout');
      var elements = selectors
        .map(elementExists)
        .filter(function (el) { return el; });

      if (elements.length === selectors.length) {
        callback.apply(this, elements);
        // console.log(elements);
      } else {
        // console.log('still waiting for elements...');
        setWaitForElements();
      }

    function elementExists(el) {
      return (typeof el === 'string' && document.querySelectorAll(el).length && document.querySelectorAll(el));
      // console.log('element exists');
    }

    function setWaitForElements() {
      total += 1;
      setTimeout(function () { waitForElementsFn(selectors, callback); }, 100);
      // console.log(total);
    }
  }, 0);
}

